import React, { useState, useRef, useCallback } from "react";
import './App.css';
import useBookSearch from './useBookSearch'

export default function App() {
  const [query, setQuery] = useState('')
  const [pageNumber, setPageNumber] = useState(1)
  const {
    books, 
    hasMore, 
    loading, 
    error
  } = useBookSearch(query, pageNumber)
  const observer = useRef()
  const lastBookElementRef = useCallback(node => {
    if (loading) return
    if (observer.current) observer.current.disconnect()
    observer.current = new IntersectionObserver(entries => {
      if(entries[0].isIntersecting && hasMore) {
        setPageNumber(prevPageNumber => prevPageNumber +1) 
      }
    })
    if (node) observer.current.observe(node)
  }, [loading, hasMore])
  function handleSearch(e) {
    setQuery(e.target.value)
    setPageNumber(1)
  }

  {/* Rendering Text */}
  return (
    <>
    {/* Title & Header */}
     <div className="App">
      <h1> SnackPass Interview Question</h1>
      <h2 align="left"> Search Bar:</h2>
    </div>

      {/* Query Search Bar */}
      <input  type="text" style={{align: 'center' }} value={query} onChange={handleSearch}></input>
      {books.map((book, index) => {
        if (books.length === index + 1) {
          return <div align="center" ref={lastBookElementRef} key={book}>{book}</div>
        } else {
          return <div align="center" key={book}>{book}</div>
        }
      })}

      {/* Loading and Error message */}
      <div align="center" style={{ color: 'red' }}>{loading && 'Loading...'}</div>
      <div align="center" style={{ color: 'red' }}>{error && 'Error'}</div>
    </>
  );
}
