import axios from "axios";
import { useState, useEffect, useRef, useCallback} from "react";

export default function App() {
  const [results, setResults] = useState([])
  const [isLoading, setIsLoading] = useState(false)
  const [isError, setIsError] = useState(false)
  const [error, setError] = useState({})
  const [hasNextPage, setHasNextPage] = useState(false)
  useEffect(() => {
    setIsLoading(true)
    setIsError(false)
    setError({})
    const controller = new AbortController()
    const { signal } = controller

    axios
      .get('./Foodsource.json')
      .then(res => { 
        setResults(res.data)
        setHasNextPage(Boolean(res.length))
        setIsLoading(false)
      })
      .catch(e => {
        setIsLoading(false)
        if (signal.aborted) return
        setIsError(true)
        setError({ message: e.message })
    })
    return () => controller.abort()
  },[]);
  const displayData = results.map(
    (info)=> {
      return(
        <tr>
          <td>{info.resteraunt}</td>
          <td>{info.name}</td>
          <td>{info.people}</td>
          <td>{info.timetag}</td>
        </tr>
      )
    }
  )
  const [pageNum, setPageNum] = useState(1)
  const intObserver = useRef()
    const lastPostRef = useCallback(post => {
        if (isLoading) return

        if (intObserver.current) intObserver.current.disconnect()

        intObserver.current = new IntersectionObserver(posts => {
            if (posts[0].isIntersecting && hasNextPage) {
                console.log('We are near the last post!')
                setPageNum(prev => prev + 1)
            }
        })

        if (post) intObserver.current.observe(post)
    }, [isLoading, hasNextPage])

    if (isError) return <p className='center'>Error: {error.message}</p>

    const content = results.map((post, i) => {
        if (results.length === i + 1) {
            return <displayData ref={lastPostRef} key={post.id} post={post} />
        }
        return <displayData key={post.id} post={post} />
    })

  return (
    <div className="App">
      <div>
      <table class="table table-striped">
        <thead>
          <tr align="left">
            <th>Food Item</th>
            <th>Resteraunt</th>
            <th>Number of times bought</th>
            <th>How recently bought</th>
          </tr>
        </thead>
        <tbody>
          {displayData}
        </tbody>
      </table>
    </div>
    </div>


  );
}
