import axios from "axios";
import { useRef, useState, useEffect} from "react";
import infiniteScroll from 'infinite-scroll';
import './App.css';

export default function App() {
    const [results, setResults] = useState([])
    useEffect(() => {
      axios
        .get('./Foodsource.json?page=2&limit=50')
        .then(res => { 
          setResults(res.data)
        })
        .catch(e => { console.log(e)}) },[])

        const displayData = results.map(
          (info)=> {
            return(
              <tr>
                <td>{info.id}</td>
                <td>{info.resteraunt}</td>
                <td>{info.name}</td>
                <td>{info.people}</td>
                <td>{info.timetag}</td>
              </tr>
            )
          }
        )

        useEffect(() => {

          const row = document.querySelectorAll(".card")

        {/* main observer */}
          const observer = new IntersectionObserver(entries => {
            entries.forEach(entry => {
              entry.target.classList.toggle("show", entry.isIntersecting)
            })
          },
          {
            threshold: 0.5,
            rootMargin: "+20px"
          }
          )


          {/* last observer */}
          const lastRowObserver = new IntersectionObserver(entries => {
            const lastRow = entries[0]
            if (!lastRow.isIntersecting) return
            const prev = document.querySelectorAll('.card').length
            for (let i = prev; i < prev + 1; i++) {
              const item = document.createElement("tbody")
              item.classList.add("card")
              item.classList.add("spacing")
              var text = " "
              for (let j = 0; j < 5; j++) {
                text += Object.values(displayData[i])[4].children[j].props.children
                text += " "
              }
              item.textContent = (text)
              observer.observe(item)
              cardContainer.append(item)
            }

            lastRowObserver.unobserve(lastRow.target)
            lastRowObserver.observe(document.querySelector(".card:last-child"))
            }, {})
            lastRowObserver.observe(document.querySelector(".card:last-child"))


          const cardContainer = document.querySelector(".card-container")

          row.forEach(val => { 
            observer.observe(val)
          }) 

        })

  return (
    <div className="App">
          <tr align="center">
            <th >ID: </th>
            <th >Resteraunts</th>
            <th >Food Item</th>
            <th>Number of times bought &nbsp;</th>
            <th>How recently bought</th>
          </tr>
      <div class="card-container" align="left">
        <tbody class="card show">{displayData.slice(0,1)} </tbody>
        <tbody class="card"> {displayData.slice(1,2)}  </tbody>
        <tbody class="card"> {displayData.slice(2,3)} </tbody>
        <tbody class="card"> {displayData.slice(3,4)} </tbody>
        <tbody class="card"> {displayData.slice(4,5)} </tbody>
        <tbody class="card"> {displayData.slice(5,6)} </tbody>
        <tbody class="card"> {displayData.slice(6,7)} </tbody>
        <tbody class="card"> {displayData.slice(8,9)} </tbody>
        <tbody class="card"> {displayData.slice(9,10)} </tbody>
      </div>
    </div>


  );
}
